/**
 * Modifies provided options to match requirements for messaging
 *
 * @param {Object} options - provided options
 */
const options = options => {
  // We do not allow eviction and handle all events sequentially
  options.pool.min = 1
  options.pool.max = 1
  options.pool.evictionRunIntervalMillis = 0
  options.pool.evictionRunIntervalMillisForPools = 0
}

module.exports = options
