const dependencies = {
  get Client () {
    const Client = require('./client/Client')
    Object.defineProperty(dependencies, 'Client', { value: Client })
    return Client
  },
  get options () {
    const options = require('./client/options')
    Object.defineProperty(dependencies, 'options', { value: options })
    return options
  },
  get messagingFunctions () {
    const messagingFunctions = require('./client/messagingFunctions')
    Object.defineProperty(dependencies, 'messagingFunctions', { value: messagingFunctions })
    return messagingFunctions
  }
}

module.exports = dependencies
